u_dev_tool_operation.u_script:
var currentRecord = current; //Current GlideRecord
var scriptIncludeName = name;
var targetScope = scope;

var scriptIncludeGr = new GlideRecord('sys_script_include');
scriptIncludeGr.initialize();
scriptIncludeGr.name = scriptIncludeName;
scriptIncludeGr.sys_scope = targetScope.getUniqueValue();

var script = 'var ' + scriptIncludeName +  ' = Class.create();';
script += '\n' + scriptIncludeName + '.prototype = Object.extendsObject(' + currentRecord.getValue('api_name') + ', {';
script += '\n\n\n\n';
script += '\n\ttype: \''+ scriptIncludeName +'\'';
script += '\n});';

scriptIncludeGr.script = script;

scriptIncludeGr.insert();

result = scriptIncludeGr;
